  and card_number = #{cardNumber}


  and openid = #{openid}

select 
from person
where status = 1

  and company_id = #{companyId}


  and card_number = #{cardNumber}


  and openid = #{openid}

select id, name, nickname, avatar_url, gender, phone, country, province, city, card_number, sign_type,
